context("Test createWKT")

test_that("Errors given", {
    expect_error(rnbn:::createWKT('tom', 'sam'), 'latitude and longitude must be numeric') 
    expect_error(rnbn:::createWKT(51.6011023, -1.1278673, 'tom'), 'radius must be numeric (meters)')
})

test_that("Success", {
    expect_equal(rnbn:::createWKT(51.6011023, -1.1278673, 10000),
                 "POLYGON ((-0.9835398400000001 51.6010135800000000, -0.9906873700000000 51.5732477000000030, -1.0112391400000000 51.5482141100000040, -1.0431688600000000 51.5283570300000020, -1.0833511199999999 51.5156125000000030, -1.1278672999999999 51.5112218499999980, -1.1723834799999999 51.5156125000000030, -1.2125657400000001 51.5283570300000020, -1.2444954600000000 51.5482141100000040, -1.2650472300000000 51.5732477000000030, -1.2721947600000001 51.6010135800000000, -1.2652147000000000 51.6287962800000030, -1.2447664400000000 51.6538738799999990, -1.2128367200000001 51.6737853599999970, -1.1725509500000000 51.6865738999999990, -1.1278672999999999 51.6909813600000020, -1.0831836500000001 51.6865738999999990, -1.0428978799999999 51.6737853599999970, -1.0109681600000000 51.6538738799999990, -0.9905199000000000 51.6287962800000030, -0.9835398400000001 51.6010135800000000))"
    )
})